	xplGlobal.document.metadata={"userInfo":{"institute":false,"member":false,"individual":false,"guest":false,"subscribedContent":false,"fileCabinetContent":false,"fileCabinetUser":false,"institutionalFileCabinetUser":false,"showPatentCitations":true,"showGet802Link":false,"showOpenUrlLink":false,"tracked":false,"desktop":false,"delegatedAdmin":false,"isInstitutionDashboardEnabled":false,"isInstitutionProfileEnabled":false,"isRoamingEnabled":false,"isDelegatedAdmin":false,"isMdl":false,"isCwg":false},"authors":[{"name":"Noura Alomar","affiliation":["College of Computer and Information Sciences, King Saud University, Riyadh, Saudi Arabia"],"bio":{"p":["Noura Alomar received the Bachelor Degree of Computer and Information Sciences in information technology from King Saud University in 2011, and the Master of Science in Software Engineering from the School of Electronics and Computer Science, University of Southampton, Southampton, U.K., in 2014. She is currently a faculty member in the Software Engineering Department, College of Computer and Information Sciences, King Saud University, Riyadh, Saudi Arabia. Her current research focuses on software project management, user authentication, gamification, crowdsourcing technologies, human computing behavior and cross-cultural user-experience design."]},"firstName":"Noura","lastName":"Alomar","orcid":"0000-0002-9052-999X","id":"37086155775"},{"name":"Mansour Alsaleh","affiliation":["King Abdulaziz City for Science and Technology, Riyadh, Saudi Arabia"],"bio":{"graphic":"/mediastore_new/IEEE/content/freeimages/9739/7936707/7814222/alsal-2651741-small.gif","p":["Mansour Alsaleh received the undergraduate degree from King Saud University in 1999, the M.S. degree from the University of Ottawa in 2006, and the Ph.D. degree from Carleton University in 2011, all in computer science. He is currently the Deputy Director of the Joint Centers of Excellence Program (JCEP) at the King Abdulaziz City for Science and Technology (KACST) and an Assistant Professor at KACST. Prior to pursuing his master studies, he spent four years in industry working in data security. His research interests lie primarily in the area of computer and network security and in the areas of big data, data mining, and machine learning. His work has spanned a range of topics in network security (intrusion detection and prevention systems, network scan detection, network security data visualization, and network protocol security), web security, authentication, applied cryptography, e-privacy, identity federation frameworks and identity management. His current research interests include web security, cloud security, and leveraging machine learning and data mining to understand and practically address some selected problems in operational real-world enterprise environments."]},"firstName":"Mansour","lastName":"Alsaleh","id":"37659836400"},{"name":"Abdulrahman Alarifi","affiliation":["King Abdulaziz City for Science and Technology, Riyadh, Saudi Arabia"],"bio":{"graphic":"/mediastore_new/IEEE/content/freeimages/9739/7936707/7814222/alari-2651741-small.gif","p":["Abdulrahman Alarifi received the Ph.D. in computer science from Syracuse University in 2007. He is currently an Associate Professor at King Abdulaziz City for Science and Technology in Saudi Arabia. He has more than nine years of experience in research in information security and machine learning. His research interests are in machine learning, wireless sensor network security, Internet security, intrusion detection, software security and cloud computing. He has published more than 50 research papers in these areas."]},"firstName":"Abdulrahman","lastName":"Alarifi","id":"38233822600"}],"issn":[{"format":"Electronic ISSN","value":"1553-877X"},{"format":"CD","value":"2373-745X"}],"articleNumber":"7814222","dbTime":"2 ms","metrics":{"citationCountPaper":14,"citationCountPatent":0,"totalDownloads":1234},"sponsors":[{"packageNumber":0,"name":"IEEE Communications Society","url":"http://www.comsoc.org/"}],"purchaseOptions":{"showOtherFormatPricingTab":false,"showPdfFormatPricingTab":true,"pdfPricingInfoAvailable":true,"otherPricingInfoAvailable":false,"mandatoryBundle":false,"optionalBundle":false,"pdfPricingInfo":[{"memberPrice":"$14.95","nonMemberPrice":"$33.00","partNumber":"7814222","type":"PDF/HTML"}]},"getProgramTermsAccepted":false,"sections":{"abstract":"true","authors":"true","figures":"true","multimedia":"false","references":"true","citedby":"true","keywords":"true","definitions":"false","algorithm":"false","dataset":"false","cadmore":"false","footnotes":"false","disclaimer":"false","relatedContent":"false","metrics":"true"},"formulaStrippedArticleTitle":"Social Authentication Applications, Attacks, Defense Strategies and Future Research Directions: A Systematic Review","keywords":[{"type":"IEEE Keywords","kwd":["Authentication","Context","Knowledge based systems","Tutorials","Taxonomy","Social network services"]},{"type":"INSPEC: Controlled Indexing","kwd":["pattern classification","social networking (online)","trusted computing"]},{"type":"INSPEC: Non-Controlled Indexing","kwd":["social authentication applications","online social networks","social relationships","socially aware authentication schemes","physical social contexts","online social contexts","knowledge-based social authentication schemes","trust-based social authentication schemes","user social interactions","defense strategies"]},{"type":"Author Keywords ","kwd":["Multi-factor authentication","social authentication","social factors","social media","systematic literature review"]}],"allowComments":false,"pubLink":"/xpl/RecentIssue.jsp?punumber=9739","abstract":"The ever-increasing volumes of social knowledge shared in online social networks, the establishment of trustworthy social relationships over these platforms, and the emergence of technologies that allow friendship networks to be inferred from data exchanged in communication networks have motivated researchers to build socially aware authentication schemes. We conduct the first study that surveys the literature related to social authentication. In this paper, we not only created a taxonomy for classifying all social authentication schemes deployed in online or physical social contexts and extensively analyzed their authentication features, but also built a novel framework for evaluating the effectiveness of all social authentication schemes, identified all the practical and theoretical attacks that may be mounted against such schemes, addressed possible defense strategies, and identified challenges, open questions, and future research opportunities. To measure their accuracy, strengths, weaknesses, and limitations, as well as to identify the potential of knowledge-based and trust-based social authentication schemes, a comprehensive comparative assessment of the security, usability, and deployability was conducted. We hope, by providing a solid foundation for gaining sufficient understanding of the manners in which users' social interactions have been utilized in user authentication schemes and their corresponding security implications, we will guide future research in this domain.","doi":"10.1109/COMST.2017.2651741","rightsLink":"http://s100.copyright.com/AppDispatchServlet?publisherName=ieee&publication=9739&title=Social+Authentication+Applications%2C+Attacks%2C+Defense+Strategies+and+Future+Research+Directions%3A+A+Systematic+Review&isbn=&publicationDate=Secondquarter+2017&author=Noura+Alomar&ContentID=10.1109/COMST.2017.2651741&orderBeanReset=true&startPage=1080&endPage=1111&volumeNum=19&issueNum=2","publicationTitle":"IEEE Communications Surveys & Tutorials","displayPublicationTitle":"IEEE Communications Surveys & Tutorials","pdfPath":"/iel7/9739/7936707/07814222.pdf","startPage":"1080","endPage":"1111","issueLink":"/xpl/tocresult.jsp?isnumber=7936707","doiLink":"https://doi.org/10.1109/COMST.2017.2651741","pdfUrl":"/stamp/stamp.jsp?tp=&arnumber=7814222","isReadingRoomArticle":false,"isGetArticle":false,"isGetAddressInfoCaptured":false,"isMarketingOptIn":false,"pubTopics":[{"name":"Communication, Networking and Broadcast Technologies"},{"name":"Signal Processing and Analysis"}],"publisher":"IEEE","xploreDocumentType":"Journals & Magazine","isSMPTE":false,"isOUP":false,"isSAE":false,"isNow":false,"isCustomDenial":false,"journalDisplayDateOfPublication":"11 January 2017","isNotDynamicOrStatic":false,"chronOrPublicationDate":"Secondquarter  2017","htmlAbstractLink":"/document/7814222/","isPromo":false,"displayDocTitle":"Social Authentication Applications, Attacks, Defense Strategies and Future Research Directions: A Systematic Review","isStandard":false,"isConference":false,"volume":"19","issue":"2","isProduct":false,"isMorganClaypool":false,"publicationDate":"Secondquarter 2017","accessionNumber":"16917915","isOpenAccess":false,"isEphemera":false,"isEarlyAccess":false,"isJournal":true,"isBook":false,"isBookWithoutChapters":false,"isChapter":false,"isStaticHtml":false,"htmlLink":"/document/7814222/","dateOfInsertion":"02 June 2017","persistentLink":"https://ieeexplore.ieee.org/servlet/opac?punumber=9739","isDynamicHtml":true,"isFreeDocument":false,"startPage":"1080","openAccessFlag":"F","ephemeraFlag":"false","title":"Social Authentication Applications, Attacks, Defense Strategies and Future Research Directions: A Systematic Review","accessionNumber":"16917915","html_flag":"false","ml_html_flag":"true","sourcePdf":"comst-alomar-2651741-x.pdf","content_type":"Journals & Magazines","mlTime":"PT0.236134S","chronDate":"Secondquarter  2017","xplore-pub-id":"9739","pdfPath":"/iel7/9739/7936707/07814222.pdf","isNumber":"7936707","rightsLinkFlag":"1","dateOfInsertion":"02 June 2017","contentType":"periodicals","publicationDate":"Secondquarter 2017","publicationNumber":"9739","citationCount":"14","xplore-issue":"7936707","issue":"2","articleId":"7814222","publicationTitle":"IEEE Communications Surveys & Tutorials","sections":{"abstract":"true