	xplGlobal.document.metadata={"userInfo":{"institute":false,"member":false,"individual":false,"guest":false,"subscribedContent":false,"fileCabinetContent":false,"fileCabinetUser":false,"institutionalFileCabinetUser":false,"showPatentCitations":true,"showGet802Link":false,"showOpenUrlLink":false,"tracked":false,"desktop":false,"delegatedAdmin":false,"isInstitutionDashboardEnabled":false,"isInstitutionProfileEnabled":false,"isRoamingEnabled":false,"isDelegatedAdmin":false,"isMdl":false,"isCwg":false},"authors":[{"name":"Fabio Angius","affiliation":["Department of Computer Science, University of California, Los Angeles, California, USA"],"firstName":"Fabio","lastName":"Angius","id":"38494406000"},{"name":"Cedric Westphal","affiliation":["Huawei, Innovation Center, Santa Clara, USA"],"firstName":"Cedric","lastName":"Westphal","id":"37293728000"},{"name":"Mario Gerla","affiliation":["Department of Computer Science, University of California, Los Angeles, California, USA"],"firstName":"Mario","lastName":"Gerla","id":"37269066800"},{"name":"Giovanni Pau","affiliation":["Department of Computer Science, University of California, Los Angeles, California, USA"],"firstName":"Giovanni","lastName":"Pau","id":"37269096100"}],"isbn":[{"format":"Electronic ISBN","value":"978-1-4799-3088-3","isbnType":""}],"articleNumber":"6849246","dbTime":"3 ms","metrics":{"citationCountPaper":0,"citationCountPatent":0,"totalDownloads":180},"purchaseOptions":{"showOtherFormatPricingTab":false,"showPdfFormatPricingTab":true,"pdfPricingInfoAvailable":true,"otherPricingInfoAvailable":false,"mandatoryBundle":false,"optionalBundle":false,"pdfPricingInfo":[{"memberPrice":"$14.95","nonMemberPrice":"$33.00","partNumber":"6849246","type":"PDF/HTML"}]},"getProgramTermsAccepted":false,"sections":{"abstract":"true","authors":"true","figures":"true","multimedia":"false","references":"true","citedby":"false","keywords":"true","definitions":"false","algorithm":"false","dataset":"false","cadmore":"false","footnotes":"true","disclaimer":"false","relatedContent":"false","metrics":"true"},"keywords":[{"type":"IEEE Keywords","kwd":["Facebook","Computer architecture","Protocols","Encryption","Message systems"]},{"type":"INSPEC: Controlled Indexing","kwd":["application program interfaces","authorisation","cryptographic protocols","data privacy","social networking (online)"]},{"type":"INSPEC: Non-Controlled Indexing","kwd":["WARP","ICN architecture","social data","social network companies","data ownership","data visibility","information-centric networking","media distribution","data control","layered design","data distribution","access policies","data replication","control channels","thread updates","data access","control policy","API","user device","data storage","user butler","data encryption","data privacy"]}],"formulaStrippedArticleTitle":"WARP: A ICN architecture for social data","allowComments":false,"pubLink":"/xpl/conhome/6841218/proceeding","abstract":"Social network companies maintain complete visibility and ownership of the data they store. However users should be able to maintain full control over their own content. For this purpose, we propose WARP, an architecture based upon Information-Centric Networking (ICN) designs, which expands the scope of the ICN architecture beyond media distribution, to provide data control in social networks. The benefit of our solution lies in the lightweight nature of the protocol and in its layered design. With WARP, data distribution and access policies are enforced on the user side. Data can still be replicated in an ICN fashion but we introduce control channels, named thread updates, which ensures that the access to the data is always updated to the latest control policy. WARP decentralizes the social network but still offers APIs so that social network providers can build products and business models on top of WARP. Social applications run directly on the user's device and store their data on the user's butler that takes care of encryption and distribution. Moreover, users can still rely on third parties to have high-availability without renouncing their privacy.","issueLink":"/xpl/tocresult.jsp?isnumber=6849127","doiLink":"https://doi.org/10.1109/INFCOMW.2014.6849246","doi":"10.1109/INFCOMW.2014.6849246","startPage":"287","endPage":"292","pdfPath":"/iel7/6841218/6849127/06849246.pdf","rightsLink":"http://s100.copyright.com/AppDispatchServlet?publisherName=ieee&publication=proceedings&title=WARP%3A+A+ICN+architecture+for+social+data&isbn=978-1-4799-3088-3&publicationDate=April+2014&author=Fabio+Angius&ContentID=10.1109/INFCOMW.2014.6849246&orderBeanReset=true&startPage=287&endPage=292&proceedingName=2014+IEEE+Conference+on+Computer+Communications+Workshops+%28INFOCOM+WKSHPS%29","displayPublicationTitle":"2014 IEEE Conference on Computer Communications Workshops (INFOCOM WKSHPS)","publicationTitle":"2014 IEEE Conference on Computer Communications Workshops (INFOCOM WKSHPS)","pdfUrl":"/stamp/stamp.jsp?tp=&arnumber=6849246","isReadingRoomArticle":false,"isGetArticle":false,"isGetAddressInfoCaptured":false,"isMarketingOptIn":false,"pubTopics":[{"name":"Communication, Networking and Broadcast Technologies"},{"name":"Computing and Processing"}],"publisher":"IEEE","xploreDocumentType":"Conference Publication","isNotDynamicOrStatic":false,"isCustomDenial":false,"isSMPTE":false,"isOUP":false,"isSAE":false,"isNow":false,"isPromo":false,"chronOrPublicationDate":"April 27 2014-May 2 2014","htmlAbstractLink":"/document/6849246/","displayDocTitle":"WARP: A ICN architecture for social data","isStandard":false,"conferenceDate":"27 April-2 May 2014","persistentLink":"https://ieeexplore.ieee.org/servlet/opac?punumber=6841218","isConference":true,"isProduct":false,"isMorganClaypool":false,"isEarlyAccess":false,"accessionNumber":"14431042","isOpenAccess":false,"isEphemera":false,"publicationDate":"April 2014","dateOfInsertion":"08 July 2014","htmlLink":"/document/6849246/","isChapter":false,"isStaticHtml":true,"isJ